#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy-.5;
    vec2 center = vec2(0);

    //uv.x=tan(abs(uv.x*10.+iTime)); - фон больше глитчит
    uv.x=sin(uv.x*10.+iTime);

    float d = distance(uv,center);
    float r = .3;

    if(d<r){
    fragColor = vec4(1,
        cos(asinh(iTime*15. + uv.x*1.))*.2+.4,
        sin(iTime*15. + uv.y*8.)*.3+.8,
        1);
    }else{
    fragColor = vec4(0,
        sin(iTime*7. + uv.x*15.)*.5+.2,
        tan(iTime*6. + uv.y*9.)*.1+.2,
        0);
    }
}
