void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec3 R = iResolution, col = vec3(.7,.1,.1);
    vec2 uv = (2.*fragCoord -R.xy)/R.y;
    float aa = 2./R.y, i, dist;
    vec4 cur;

    for(;i<5.; i+=2.1){
        dist = abs(length(uv - .35*vec2(sin(i), cos(i)-.2)) - .5) - .08;

        cur = vec4((col = col.zxy) - dist, smoothstep(-aa,aa,-dist));
        if( i > 3. && fragColor.g > fragColor.b && cur.w > .0 )
            cur.w = min(1. - fragColor.w, cur.w);

        fragColor = mix(fragColor, cur, cur.w);       
    }

    fragColor = mix(vec4(1.-length(uv)/2.8), fragColor, fragColor.w);
}
