#define PI 3.1415

//Mess with the values here
#define SPEED 1.5
#define SCALE 4.

//HEIGHT gets breaks when the values exceed 2 or is negative
#define HEIGHT .0
#define DIP .5

vec3 getTexture (vec2 coord) {


   return (coord.x < 0. || coord.x > 1. || coord.y < 0. || coord.y > 1. ? 0. : 1.) * texture(iChannel0, coord.xy).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= SCALE;

    float t = mod(iTime*SPEED,2.0), p = 0.;

    vec3 newCol = vec3(0);
    vec2 fuv;

    for (int x = 0; x <= 2; x++){
        for (int y = -2; y <= 0; y++){
            vec2 iuv = floor(uv) + vec2(float(x), float(y));

            float a = min(mod(mod(iuv.y*1.+iuv.x*1.,2.0)+t,2.0),1.);
            float b = a*a*(3.-2.*a);

            vec2 puv = uv + vec2(a*2.,-HEIGHT*abs(1.+sin(a*PI))), coord = (puv-iuv)*abs(1.+sin(a*PI)*DIP);
            if (a > p && coord.x > 0. && coord.x < 1. && coord.y > 0. && coord.y < 1.){
                fuv = (puv-iuv);
                p = a;
                newCol = texture(iChannel0, fuv).xyz;
            }


        }
    }

    /*
    vec2 iuv = floor(uv);

            float a = min(mod(mod(iuv.y*2.+iuv.x*1.,2.0)+t,2.0),1.);

            uv += vec2(a*2.,-abs(sin(a*PI)));
    */

    //uv += vec2(a,-abs(sin(a*3.1415)));
    //vec3 col = vec3(a);
    vec3 col = texture(iChannel0, fuv).xyz;

    // Output to screen
    fragColor = vec4(newCol,1.0);
}
