void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centeredUV = (fragCoord-iResolution.xy*0.5)/iResolution.x;

    float fov = 0.05;

    vec3 camPos = vec3(sin(iTime*5.)*0.6, cos(iTime*7.)*0.2, iTime*3.5);
    vec3 viewDir = vec3(centeredUV, fov);

    vec3 pipe = viewDir / length(viewDir.xy) +camPos;
    float cylindrical = atan(pipe.x, pipe.y);

    fragColor = texture(iChannel0, pipe.xz);
    //fragColor = vec4(cylindrical);
    fragColor = texture(iChannel0, vec2(pipe.z, cylindrical)).abra * 0.06/length(centeredUV);
}
