// Golfed to 258 characters by @FabriceNeyret2 , below two tweets character limit!
void mainImage(out vec4 c, vec2 v) {
    c.xyz = iResolution;
    v = (v+v - c.xy) * 1.54 / min(c.x, c.y);
    c = vec4(0, .1, .5, 1);
    for(float a; a < 6.; a += .5246) {
        mat2 z =  mat2( v.y, -v.x, v )
                - mat2(cos(a+vec4(0,11,33,0)));
        c += smoothstep(1.8, 1.1, determinant( z*z*z*z*z*8e4 - mat2(1) )) 
             * vec4(1, .8, -.5, 1);
    }
}


