void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Constant variables
    const float frequncy = 5.0;
    const float xMuliplyer = 30.0;
    const float yMuliplyer = 15.0;

    //Color vectors
    vec4 colA = vec4(0.5,0.0,0.5,1.0);
    vec4 colB = vec4(0.0,0.7,1.0,1.0);


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    //Alters the UV x and y coords by muliplying by sin
    float wave = tan(uv.x * xMuliplyer + uv.y * yMuliplyer + iTime * frequncy) * 0.1 + 0.4;
    wave = wave * tan(uv.x / 0.6);


    // Calculate texture coordinates
    vec2 texCoords = uv * 4.0;
    texCoords = mod(texCoords, 8.0);

    // Sample the texture
    vec4 textureColor = texture(iChannel0, texCoords);

    //Smooths out the edges of the wave
    float t = smoothstep(0.4,0.6,wave);
    t = fract(t);


    // mixes the color, and shader together
    fragColor = mix(colB, textureColor, t);
}
