#define PI 3.14159
#define nmc(x) (-cos(x) * 0.5 + 0.5)

float sdHexTiling(vec2 position) {
    // from iq https://www.shadertoy.com/view/fdtGDH
    const vec2 k = vec2(2.0, sqrt(3.0));
    position /= k;
    position.y -= 0.5;
    position.x -= fract(floor(position.y) * 0.5);
    position = abs(fract(position) - 0.5);
    return abs(1.0 - max(position.x + position.y * 1.5, position.x * 2.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 pos = (fragCoord - iResolution.xy / 2.) / iResolution.y * 2.;
    float t = iTime * 0.02;
    vec2 mouse = iMouse.xy / iResolution.xy * 2. - 1.;

    float theta = iTime * 0.03 + 9.;
    float c = cos(theta);
    float s = sin(theta);
    mat2 rot = mat2(c, s, -s, c);

    vec2 hexPos = rot * (pos - 2. * mouse);
    hexPos = hexPos + t;
    hexPos += sin(hexPos * PI * cos(t * 10.)) * 0.1;

    float sd = abs(sdHexTiling(hexPos * 3.));
    sd = pow(sd, 2.);

    vec3 c0 = vec3(1., 0.05, 0.6);
    vec3 c1 = vec3(1., 0.8, 0.2);

    float m0 = dot(c0, normalize(vec3(hexPos, 0.)));

    vec3 c2 = mix(c0, c1, m0);

    float e = 1e-1;
    float v = smoothstep(0.5 - e, 0.5 + e, nmc(sd * PI * 10. + iTime));

    vec3 tex = texture(iChannel0, fract(rot * hexPos * 0.4)).rgb * 1.2 + 0.1;

    vec3 col = mix(c0, c1, v) * mix(1., tex.r * 4. + 1., 0.05);

    fragColor = vec4(col, 1.0);
}
