// 0 = Distance Field Display
// 1 = Raymarched Edges
// 2 = Resulting Solid
#define DISPLAY 0

#define DEBUG

const vec3 POSITIVE_BG = vec3( 0.3843, 0.4471, 0.6431 );
const vec3 NEGATIVE_BG = vec3( 1.0   , 0.3333, 0.3333 );
const vec3 DISTANCE    = vec3( 0.9451, 0.9804, 0.5490 );
const vec3 BORDER      = vec3( 0.9725, 0.9725, 0.9490 );

// Combined signed distance function for the shape
float
map( in vec2 p )
{
    float dCircle = 0.0;
    float dBox    = 0.0;

    // Moving Circle
    {
        // xy position. z radius
        vec3 c  = vec3( sin( iTime ) * 1.5, 0, 0.5 );
        dCircle = sdCircle( p - c.xy, c.z );
    }

    // Static Box
    {
        // xy position. zw size
        vec4 b = vec4( 0, 0, cos( iTime ), 0.25 );
        dBox   = sdBox( p - b.yx, b.zw );
    }

    return smin( dCircle, dBox, 0.5 );
}

void
mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = ( fragCoord * 2. - iResolution.xy ) / iResolution.y;
    vec2 m = ( iMouse.xy * 2. - iResolution.xy ) / iResolution.y;

    float d = map( p );

    vec3 col = vec3(0.0);

    // Coloring
#if DISPLAY == 0
    col = ( d > 0.0 ) ? POSITIVE_BG : NEGATIVE_BG;
    col *= 1.0 - exp( -6.0 * abs( d ) );
    col *= 0.8 + 0.2 * cos( 150.0 * d + ( iTime * 15.0 * d / abs( d ) ) );
    col = mix( col, BORDER, 1.0 - smoothstep( 0.0, 0.01, abs( d ) ) );
#endif
#if DISPLAY == 1
    col = mix( POSITIVE_BG, BORDER, 1.0 - smoothstep( 0.0, 0.01, abs( d ) ) );
#endif
#if DISPLAY == 2
    col = ( d > 0.0 ) ? POSITIVE_BG : NEGATIVE_BG;
    col = mix( col, BORDER, 1.0 - smoothstep( 0.0, 0.01, abs( d ) ) );
#endif

#ifdef DEBUG
    if ( iMouse.z > 0.0 )
        {
            d = map( m );
            col = mix( col, DISTANCE, 1.0 - smoothstep( 0.0, 0.005, abs( length( p - m ) - abs( d )) - 0.0025 ) );
            col = mix( col, DISTANCE, 1.0 - smoothstep( 0.0, 0.005, length( p - m ) - 0.015 ) );
        }
#endif

    fragColor = vec4( col, 1.0 );
}

// --------------------------------------------------------

float smin( float a, float b, float k )
{
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

// https://iquilezles.org/articles/distfunctions2d/
// --------------------------------------------------------

float sdCircle(in vec2 p, in float r)
{
    return length(p) - r;
}

float sdBox(in vec2 p, in vec2 b)
{
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}
