void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float m = 1064.;

    float n = fract(sin(uv.x*m+uv.y/(m*20.))*(iTime * 55344.));

    vec3 col = vec3(n);

    fragColor = vec4(col,1.0);
}

vec2 mainSound( int samp, float time )
{
    float m = 1064.;

    float n = fract(sin(m/time*134.)*time*20.)*1053.;
    return vec2( sin(6.2831*n*time)*exp(-0.1*max(n, 1.)) );
}
