// https://scrapbox.io/RenardWork/GLSL%E3%81%AEfloat%E3%81%AB%E3%81%A4%E3%81%84%E3%81%A6
void mainImage(out vec4 O,vec2 FC)
{
    float pi=uintBitsToFloat(0xFFu<<23);//+inf
    float mi=uintBitsToFloat(0xFFu<<23|1u<<31);//-inf
    float nan=uintBitsToFloat(0xFFu<<23|1u);//nan
    float nan2=uintBitsToFloat(0xFFu<<23|0xffffffu);//nan2
    vec3 pic=isinf(pi)?vec3(0,0,1):vec3(1,0,0);
    vec3 mic=isinf(mi)?vec3(0,0,1):vec3(1,0,0);
    vec3 nanc=isnan(nan)?vec3(0,0,1):vec3(1,0,0);
    vec3 nan2c=isnan(nan2)?vec3(0,0,1):vec3(1,0,0);
    vec2 uv=FC/iResolution.xy;
    // x align
    vec3 c=vec3[](pic,mic,nanc,nan2c)[int(uv.x*4.)];
    O=vec4(c,1);
}
