void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;



    fragColor.rgb = vec3(0.);

    if(int(fragCoord.x-.5) % 2 == 0 && int(fragCoord.y-.5) % 2 == 0) // GREEN BOTTOM LEFT
    {
    fragColor.g = texture(iChannel0,uv).g;

    fragColor.b += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y+1.)/iResolution.y)).b;
    fragColor.b += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y-1.)/iResolution.y)).b;
    fragColor.b /= 2.;

    fragColor.r += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,fragCoord.y/iResolution.y)).r;
    fragColor.r += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,fragCoord.y/iResolution.y)).r;
    fragColor.r /= 2.;
    } // GREEN 1 IS DONE!!!!
    else if( int(fragCoord.x-.5) % 2 == 1 && int(fragCoord.y-.5) % 2 == 1) // GREEN TOP RIGHT
    {
    fragColor.g = texture(iChannel0,uv).g;

    fragColor.r += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y+1.)/iResolution.y)).r;
    fragColor.r += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y-1.)/iResolution.y)).r;
    fragColor.r /= 2.;

    fragColor.b += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,fragCoord.y/iResolution.y)).b;
    fragColor.b += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,fragCoord.y/iResolution.y)).b;
    fragColor.b /= 2.;
    } // GREEN 2 IS DONE!!!!
    else if(int(fragCoord.x-.5) % 2 == 0 && int(fragCoord.y-.5) % 2 == 1) // RED  BOTTOM RIGHT
    {
    fragColor.r = texture(iChannel0,uv).r;

    fragColor.g += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,fragCoord.y/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,fragCoord.y/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y-1.)/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y+1.)/iResolution.y)).g;
    fragColor.g /= 4.;

    fragColor.b += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,(fragCoord.y+1.)/iResolution.y)).b;
    fragColor.b += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,(fragCoord.y+1.)/iResolution.y)).b;
    fragColor.b += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,(fragCoord.y-1.)/iResolution.y)).b;
    fragColor.b += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,(fragCoord.y-1.)/iResolution.y)).b;
    fragColor.b /= 4.;
    } // RED IS DONE!!!!!!
    else if(int(fragCoord.x-.5) % 2 == 1 && int(fragCoord.y-.5) % 2 == 0) // BLUE TOP LEFT
    {
    fragColor.b = texture(iChannel0,uv).b;

    fragColor.g += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,fragCoord.y/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,fragCoord.y/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y-1.)/iResolution.y)).g;
    fragColor.g += texture(iChannel0,vec2(fragCoord.x/iResolution.x,(fragCoord.y+1.)/iResolution.y)).g;
    fragColor.g /= 4.;

    fragColor.r += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,(fragCoord.y+1.)/iResolution.y)).r;
    fragColor.r += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,(fragCoord.y+1.)/iResolution.y)).r;
    fragColor.r += texture(iChannel0,vec2((fragCoord.x-1.)/iResolution.x,(fragCoord.y-1.)/iResolution.y)).r;
    fragColor.r += texture(iChannel0,vec2((fragCoord.x+1.)/iResolution.x,(fragCoord.y-1.)/iResolution.y)).r;
    fragColor.r /= 4.;
    } // BLUE IS DONE!!!!!
}
