#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(-1,3,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);
    p.yz*=R2D(iTime*.5);
    p=R3D(iTime*.3);

    for(j=0.;j++<2.;)
        p=abs(abs(p*q*cos(i)*tan(iTime*.5))-.05)-.03,
        d=max(min(p.z,p.y),p.x),
        e-=d;

}o+=vec4(log(p.xy),5,1)/i;
}
