void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //retarget frag for simplifying texture
    vec2 shortFrag = fragCoord / 3.0;
    vec2 newFrag = floor(shortFrag) * 3.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = newFrag/iResolution.xy;

    //get col from texture
    vec3 col = texture(iChannel0, uv).rgb;

    //further split the 3*3 squares into strips of r, g or b

     //bonus split to mitigate banding
     if(mod(newFrag.x, 2.0) == 0.0){
        if(fract(shortFrag.y) > 0.6){
         col = vec3(col.r, 0.0, 0.0);
        } else if(fract(shortFrag.y) > 0.3){
         col = vec3(0.0, col.g, 0.0);
        } else {
         col = vec3(0.0, 0.0, col.b);
        };
     } else {
        if(fract(shortFrag.y) > 0.6){
         col = vec3(0.0, 0.0, col.b);
        } else if(fract(shortFrag.y) > 0.3){
         col = vec3(col.r, 0.0, 0.0);
        } else {
         col = vec3(0.0, col.g, 0.0);
        };
     };

    // Output to screen
    fragColor = vec4(col,1.0);
}
