const float PI = 3.1415926;

float smooth_union(float d1, float d2, float k) { 
	float h = clamp(0.5 + 0.5 * (d2 - d1) / k, 0.0, 1.0); 
	return mix(d2, d1, h) - k * h * (1.0 - h); 
} 

void mainImage(out vec4 fragColor, vec2 fragCoord) {
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y * 3.0;

    vec2 fuv = abs(fract(uv - 0.5) * 2.0 - 1.0);
    float k = max(1.95 * smooth_union(fuv.x, fuv.y, 0.3), length(fuv));
    vec3 col = vec3(k);
    col.r = smoothstep(1.0, 0.4, col.r);
    col.b = smoothstep(col.r, 1.0, col.b);
    col.b = smoothstep(col.r, 1.0, col.b * 0.9);
    col.g += smoothstep(1.0, 0.5, col.b);
    col.r = smooth_union(fuv.x, fuv.y, 0.8);
    col = 2.0 - col;
    col.b = 0.0;
    vec2 ruv = fragCoord.xy / iResolution.xy;
    col *= 0.75;
    col.rb += smoothstep(0.25 + 0.1 * k, 0.25, ruv.x);
    col.rb = mix(col.rb, col.br, smoothstep(0.69, 0.71 + k * 0.1, ruv.x));
    col = vec3(col);

    fragColor = vec4(col, 1);
}
