mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);
    float t = iTime;

    vec2 n = vec2(0);
    vec2 q = vec2(0);
    vec2 p = uv;
    float d = dot(p,p);
    float S = 60.;
    float a = 0.0;
    mat2 m = rotate2D(5.);

    float asd = 0.0;

    for (float j = 0.; j < 16.; j++) {
        p *= 0.9*m;
        n *= m;
        q = p * S + t * 4. + sin(t * 4. - d * 5.) * 0.8 + j + n;
        a += dot(cos(q)/S, vec2(.2));
        n += sin(q);
        S *= 1.2;

        q *= 0.01;

        asd += floor(q.x + q.y);
    }

    float val = mod(asd, 100.5) * 0.01;

    col = pal( val, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );

    col *= (a + 0.8) + 20.0 * a;

    fragColor = vec4(col,1.0);
}
