// rainbow color / stepped gradient logic for coloring the rainbow is from https://www.shadertoy.com/view/NsjGRG

#define COLOR1 vec4(0.95,0.0,0.0,1.0)
#define COLOR2 vec4(0.95,0.0,0.0,1.0)
#define COLOR3 vec4(1.0,141.0/255.0,0.0,1.0)
#define COLOR4 vec4(1.0,141.0/255.0,0.0,1.0)
#define COLOR5 vec4(1.0,0.95,0.0,1.0)
#define COLOR6 vec4(1.0,0.95,0.0,1.0)
#define COLOR7 vec4(0.0,129.0/255.0,33.0/255.0,1.0)
#define COLOR8 vec4(0.0,129.0/255.0,33.0/255.0,1.0)
#define COLOR9 vec4(0.0,76.0/255.0,1.0,1.0)
#define COLOR10 vec4(0.0,76.0/255.0,1.0,1.0)
#define COLOR11 vec4(118.0/255.0, 1.0/255.0, 136.0/255.0, 1.0)
#define COLOR12 vec4(118.0/255.0, 1.0/255.0, 136.0/255.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 og_uv = fragCoord.xy / iResolution.xy;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz;


    float edge = 0.5;

    float sinof = sin(og_uv.x * 20.0 + iTime * 4.0);
    float offset = (step(0.0, sinof) * 2.0 - 1.0) * 0.2;

    edge += offset * 0.05;

    float width = 0.1;
    float window = step(edge - width, og_uv.y) * step(og_uv.y, edge + width);

    float mask = window * step(nyan.w, 0.0) * step(og_uv.x, 0.8);

    float p = (og_uv.y - edge + width) / (width * 2.0);

    float step1 = 0.0;
    float step2 = 0.08333;
    float step3 = 0.1667;
    float step4 = 0.25;
    float step5 = 0.3333;
    float step6 = 0.4167;
    float step7 = 0.5;
    float step8 = 0.5833;
    float step9 = 0.6667;
    float step10 = 0.75;
    float step11 = 0.8333;
    float step12 = 0.9167;

    p = 1.0 - p;
    vec4 rainbow_c = COLOR1;
    rainbow_c = mix(rainbow_c, COLOR2, step(step2, p));
    rainbow_c = mix(rainbow_c, COLOR3, step(step3, p));
    rainbow_c = mix(rainbow_c, COLOR4, step(step4, p));
    rainbow_c = mix(rainbow_c, COLOR5, step(step5, p));
    rainbow_c = mix(rainbow_c, COLOR6, step(step6, p));
    rainbow_c = mix(rainbow_c, COLOR7, step(step7, p));
    rainbow_c = mix(rainbow_c, COLOR8, step(step8, p));
    rainbow_c = mix(rainbow_c, COLOR9, step(step9, p));
    rainbow_c = mix(rainbow_c, COLOR10, step(step10, p));
    rainbow_c = mix(rainbow_c, COLOR11, step(step11, p));
    rainbow_c = mix(rainbow_c, COLOR12, step(step12, p));

    //vec3 rainbow_c = mix(vec3(0.0), vec3(1.0, 0.0, 0.0), p);

    col = mix(col, rainbow_c.rgb, mask);

    fragColor = vec4( col, 1.0 );
}
