#define IMAX   300.
#define SMOOTH 0

void mainImage(out vec4 O, vec2 U) {
  vec2 R = iResolution.xy;
  U = (U * 2. - R) / R.y;

  vec2 c = vec2(-.8, .156);

  float i = 1.;
  for(; i <= IMAX && dot(U, U) < 6.; i++) {
    U = mat2(U, -U.y, U.x) * U + c;
  }

  #if SMOOTH
  float zd = sqrt(dot(U, U));
  i -= log2(max(1., log2(zd)));
  #endif

  // color palette
  float t = i / IMAX;
  vec3 col = t * (1. +
             vec3(1, .7, 0) *
             (1. - t));

  O = vec4(col, 1);
}
