void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)  / iResolution.y;
    vec2 uv0 = uv;

    vec3 resultado;


    uv *= 2.;

    uv = fract(uv);
    uv -= 0.5;

    vec3 color = vec3(0.141,1.000,0.078);
    vec3 color2 = 0.5 + 0.5 * cos(length(uv0) + iTime + uv.xyx + vec3(1.000,0.000,0.000));

    float d = length(uv);
    float d2 = length(uv);

    d2 -= 0.5;
    d2 = sin(d2*8. +iTime *5.)/-8.;
    d2 = abs(d2);
    d2 = 0.02 / d2;

    color2 *= d2;



    resultado = ( resultado + color2);


    fragColor = vec4(resultado,1);
}
