void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 color = texture(iChannel0,uv);
    color = vec4(0.8*sin(iTime),0.3,0.9 * cos(iTime),1.0);

    vec3 delta_bcs = vec3(1.0,0.95,1.0);
	float gray = dot(color.rgb , vec3(0.2125, 0.7154, 0.0721));
	color.rgb = mix(vec3(gray), color.rgb, delta_bcs.z);
	vec3 final = mix(vec3(0.5), color.rgb, delta_bcs.y) * delta_bcs.x;

    if(uv.x < 0.5){
        fragColor = vec4(final, color.a); //左边bcs
    }else{
        fragColor = vec4(color.rgb, color.a); //右边原图直出
    }

    if (uv.x>0.46 && uv.x < 0.51){
        fragColor = vec4(vec3(0.0), color.a);
    }
}
