renderpass.code:
float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float dist(vec2 uv,vec2 pos)
{
    float dist =  abs(pos.x-uv.x) + abs(pos.y-uv.y);
    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.x;

    vec2 pos[6];

    pos[0] = vec2(0.5,0.5);
    pos[1] = vec2(0.5,0.0);
    pos[2] = vec2(0.8,0.3);
    pos[3] = vec2(0.2,0.3);
    pos[4] = vec2(0.5,0.8);
    pos[5] = vec2(0.5,0.0);

    float value = 0.0;

    for(int i = 0; i<6;i++)
    {
        float fi = float(i);
        float d = -dist(uv,pos[i]) + (iTime*1.0-fi*10.0)*0.1;
        d = mod(d, 5.0);

        value +=  smoothstep(0.0,0.0,mod(smoothstep( 0.0,0.02,d),1.0));
        if(d < 0.5){value += smoothstep( 0.2,0.2,d);}

    }

    fragColor = vec4(smoothstep(0.5,0.5, mod(value,1.9)));
}
