{


    vec2 uv = fragCoord /iResolution.xy;

    vec2 uv0= uv;



   uv = uv-0.5;
   uv *= 2.0;

   uv.x = uv.x*(iResolution.x/iResolution.y);

 //  uv.x -=  (iResolution.x/iResolution.y) /2.0 ;
  // uv.y -= 0.5;


  //uv *=2.;

  uv = fract(uv);
  uv -=0.5;
  uv*=2.;

   float d = length(uv0 + iTime);//(0,1)
    vec3   col = palette(d+iTime);
   d = sin(d * 8.0+ iTime );//(-1,1)

   d = abs(d);//(0,1)
   //d = smoothstep(0.0,0.1,d);
   d = 0.1/d;//(0.1,+00)
   d = clamp(d,0.0,1.0);

  col *=d;


    fragColor = vec4(col,1.0);
}
