#define S smoothstep
#define L length
#define R iResolution.xy
#define T iTime

void mainImage( out vec4 o, in vec2 i )
{
    vec2 u = (i-.5*R)/R.y,
         m = vec2(sin(u.x + T * 3.) * .6, cos(u.y + T * 2.5) * .6);
    u += m * .5;

    o = vec4(
        S(S(0., 1., L(u-m) - .3), 0., abs(L(u - S(0.5, 1., L(m)) * .02) - .32) - .007),
        S(S(0., 1., L(u-m) - .3), 0., abs(L(u) - .32) - .007),
        S(S(0., 1., L(u-m) - .3), 0., abs(L(u + S(0.5, 1., L(m)) * .02) - .32) - .007),
        1.
    );
}
