void mainImage( out vec4 o, vec2 u )
{
    o = iResolution.xyxx;
    u = (o.xy - u - u) / o.y;  
    u.x *= 1.7;       
    for (u.y -= .2; o.z > 1.;    
        o = vec4(
          abs(.8 - abs(u.x - .2) + u.y) < .2
          && u.y < (u.x < 0. ? .2 : 2. - u.x)          
        , o))        
        u *= -.5 * mat2(1,-3,1,1);                
}


