// from https://www.shadertoy.com/view/XdGfRR
vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p))*uvec2(1597334673U, 3812015801U);
	q = (q.x ^ q.y) * uvec2(1597334673U, 3812015801U);
	return vec2(q) * 2.328306437080797e-10;
}
float get_luma(vec4 color){return dot(color, vec4(.299,.587,.114,1.));}

#define STEPS 8
float get_dots(vec2 p, float t)
{
    vec2 o = hash22(floor(p))*(.05*t);
    return length(fract(p)-.5 + o) - (1.-t);
}

void mainImage(out vec4 O, in vec2 U)
{
    vec2 ires = iResolution.xy;
    vec2 uv = U/ires;
    vec2 guv = (U-.5*ires)/ires.y;

    vec4 c = floor(texture(iChannel0, uv)*float(STEPS))/float(STEPS);
    float t = get_luma(c);
    float m = smoothstep(0.75, 0.85, get_dots(guv*96., t));

    O = vec4(mix(vec3(.4, .02, .1), vec3(1., .65, .8), vec3(m)), 1.);
}
