precision highp float;
#define MAX 150.0
#define r iResolution
void mainImage(out vec4 o, in vec2 fc) {
    vec2 uv = (2.0 * fc.xy - r.xy) / r.y + vec2(-0.5, 0.);
    vec2 z = vec2(0.);
    float i;
    for(i = 0.0; i < MAX && length(z) <= 2.0; i++)
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + uv;
    o = vec4(pow(vec3(i / MAX) - step(2.2, length(z)),vec3(0.4545)), 1.0);
}
