#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

float PI = 3.14159;
float TAU = 2.*3.14159;

// Thanks IQ!
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   vec3 col = vec3(0.);   
   float tt = fract(.3*iTime);

   // Stars
   vec2 uv0 = uv;  
   vec2 uvStars = uv * 30.;
   vec2 starCell = floor(uvStars);
   uvStars = fract(uvStars) * 2. - 1.;
   float r1 = fract(sin(dot(starCell, vec2(1.0,12.0)))*15745.7342) * 2. - 1.;
   float r2 = fract(sin(dot(starCell, vec2(1.0,43.0)))*13131.8234) * 2. - 1.;
   float spark = 0.;
   if (r2 > .3) spark = .1 * sin(TAU*(tt + r1));     
   float ssize = r2 * .2;  
   float star = length(uvStars - .7*vec2(r1,r2)) - ssize+spark;
     col += smoothstep(.1,-.1,star);

   // Grid + Grid Wave
   float scale = 30.;
   uv *= scale;
   vec2 cellID = round(uv);
   uv = fract(uv + .5) - .5;

   if (abs(cellID.x) <= 30. && abs(cellID.y) <= 10.){
    float squareDim = .2 + .175*sin(PI + TAU*(tt) + length(2.*uv0 - vec2(-.5,.5)));
    float sdf = sdBox(uv, vec2(squareDim,squareDim));
    if (abs(cellID.y - 6.*sin(TAU*tt+cellID.x/2.) * cos(TAU*tt + cellID.x/4.)) > 1.)
    col += smoothstep(.1,-.1,sdf);
   } 

   // Line Waves
   uv0 += vec2(0.,-.42);
   for(float i = 1.; i < 4.; i++)
   if(abs(uv0.y - (.03 + .01*sin(i+TAU*tt + (3.+10.*i)*uv0.x))*sin(TAU*tt+8.*uv0.x + 9.*i)) < .0015){
     col += 1.0;
   }

   uv0 += vec2(0.,+.84);
   for(float i = 1.; i < 4.; i++)
   if(abs(uv0.y - (.03 + .01*sin(i+TAU*tt + (7.+10.*i)*uv0.x))*sin(TAU*tt+8.*uv0.x + 5.*i)) < .0015){
     col += 1.0;
   }

   fragColor = vec4(col,1.0);
}
