vec3 hash33(vec3 p)
{
	uvec3 q = uvec3(ivec3(p)) * uvec3(1597334673U, 3812015801U, 2798796415U);
	q = (q.x ^ q.y ^ q.z)*uvec3(1597334673U, 3812015801U, 2798796415U);
	return vec3(q) * 2.328306437080797e-10;
}

float sd_circle(vec2 p, float r){ return length(p)-r; }

float sd_line(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}

float sdf(vec2 p)
{
    return sd_line(p, vec2(-2,-1), vec2(2,1));
    //return sd_circle(p, 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float d = sdf(uv*8.);
    float a = .2/(pow(d,.7));
    float t = smoothstep(1., 1.1, a);
    vec3 col = a * (0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)));
    col += .03*(hash33(vec3(fragCoord, iTime*1000.))*2.-1.);
    col = mix(col, vec3(1.), t);

    fragColor = vec4(col,1.0);
}
