float height(in vec2 uv) {
    return texture(iChannel0,uv).b*texture(iChannel1,uv+vec2(0.0,iTime*0.1)).b;
}

const vec2 NE = vec2(0.05,0.0);
vec3 normal(in vec2 uv) {
    return normalize(vec3(height(uv+NE.xy)-height(uv-NE.xy),
                          0.0,
                          height(uv+NE.yx)-height(uv-NE.yx)));
}

const vec4 waterColor = vec4(0.1,0.1,0.32,1.0);
vec3 lightDir = normalize(vec3(10.0,15.0,5.0));

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float tiles = 6.0;
    vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5);
    uv.y *= iResolution.y / iResolution.x;
    uv *= tiles;

    vec2 id = floor(uv); 
    uv = fract(uv) - 0.5;

    float dist = length(uv);
    float angle = atan(uv.y, uv.x);


    float direction = mod(id.x + id.y, 2.0) == 0.0 ? 1.0 : -1.0;

    vec2 ruv = uv;
    uv = vec2(cos(angle + dist * 3.0 * direction), dist + (iTime * 0.2));

    float h = height(uv);
    vec3 norm = normal(uv);
    fragColor = 
        mix(vec4(0.0), 
            mix(mix(waterColor + waterColor * max(0.0, dot(lightDir, norm)) * 0.1,
                    texture(iChannel0, uv), 0.2),
                texture(iChannel0, norm.xz * 0.5 + 0.5), 0.3),
            min(1.0, length(ruv) * 10.0));
}
