void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 z = fragCoord.xy / iResolution.xy;
    z -= 0.5;
    z.x *= iResolution.x/iResolution.y;
    z *= 1.;

    float t = iTime;
    vec2 w;
    float scale = 1.;

    for(int i =0; i < 25; i++) {
        vec2 w=z;
        if (z.x+z.y>=0.) {w -= 0.25; w *= mat2(cos(t), sin(t), -sin(t), cos(t)); w *= 1.414; scale *= 1.414;}
        if (z.x+z.y<=0.) {w *= 2.; w+=0.5; scale *= 2.;}
        z=w;
    }
    float d = max(abs(z.x), abs(z.y));
    vec3 col = vec3(1.-smoothstep(0.0, 0.001, d/scale));
    fragColor = vec4(col, 1.);
}
