float col;
float t;
float x;
float y;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    x = 0.5-(fragCoord.x/iResolution.x);
    y = 1.2-(fragCoord.y/iResolution.y);
    t = x+y*iResolution.x;


     col = float(
                 int(
                     sin(iTime+(x*1000.)/(.1+t/iResolution.x*50.))*
                     sin(iTime*20.-(y*1000.)/(.1+t/iResolution.y))+
                     sin(iTime*2.)+1.5
                     )&1
                  )-0.22/y;

    // Output to screen
    fragColor = vec4(0.,col,0.,0.);
}
