// You can casually change the value of
// the large letter variable here.
// 这里的大写变量的值可以随意更改
vec3 COLOR1 = vec3(1.0,0.2,0.3);
vec3 COLOR2 = vec3(0.9,0.9,0.2);
float RADIUS = 60.0;
vec2 LAYOUT = vec2(90.0,90.0);
vec2 BIAS = vec2(90.0,90.0);
float ALPHA = 1.0;



float math_step(float f,float len){
    return ceil(f/len) * len;
}


vec3 circle_repeat(vec2 fc, float radius, vec2 _layout, vec2 bias){
    vec2 my_circle_point = ceil( fc / (2.*_layout) ) * 2.*_layout - bias;

    if (length(fc-my_circle_point) <= radius){
        return COLOR1;
    }
    else
        {return COLOR2;}
}


void mainImage( out vec4 fragColor, in vec2 fragCoor)
{
    fragColor = vec4(circle_repeat(fragCoor,RADIUS,LAYOUT,BIAS),ALPHA);
}
