//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

float between(float low, float high, float y) {
    return step(low, y) - step(high, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);
    float scopeY1 = between(.65, .75, uv.y);
    float scopeY2 = between(.45, .55, uv.y);
    float scopeY3 = between(.25, .35, uv.y);
    float scopeX = between(.05, .95, uv.x);
    col = hsb2rgb(vec3(-tan(iTime/10.)+uv.x, 1.0, min((scopeY1+scopeY2+scopeY3)*scopeX, 1.0)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
