void mainImage(out vec4 o, vec2 u) 
{
    float t=iTime*.3;
    u=vec2(u.x-.5*iResolution.x,u.y-.5*iResolution.y);
    vec2 r=u*mat2(cos(t),sin(t),sin(t),cos(t));
    o = vec4((abs(int(u.x))&abs(int(u.y)))&((abs(int(r.x)))&(abs(int(r.y))))); 
}
