void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;;
    float loop_time = 4.0;
    float oscilator = (1.0+sin(2.0*3.141*mod(iTime,loop_time)/loop_time))/2.0;

    float top = max(mod(uv.x,uv.y*oscilator),mod(-uv.x,uv.y*oscilator));
    float r_side = max(mod(uv.y,uv.x*oscilator),mod(-uv.y,uv.x*oscilator));
    float l_side = max(mod(uv.y,-uv.x*oscilator),mod(-uv.y,-uv.x*oscilator));
    float sides = max(l_side, r_side);
    float bot = max(mod(-uv.x,-uv.y*oscilator),mod(uv.x,-uv.y*oscilator));
    float r = max(1.0-oscilator,top*3.0);
    float g = max(1.0-oscilator,sides*3.0);
    float b = max(1.0-oscilator,bot*3.0);

    vec3 col = vec3(r,g,b);
    // Output to screen
    fragColor = vec4(col,1.0);
}
