void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 col = texture(iChannel0, uv);
    fragColor = vec4(col.r, col.g, col.b, 1.0);
}

vec4 readTexel(in vec2 pos) {
    return texture(iChannel0, pos / iChannelResolution[0].xy);
}


void mainImage( out vec4 fragColor, in vec2 coord )
{
    vec2 pos = iMouse.xy;

    float old = readTexel(coord).r;

    float red = 0.99 * old;

    float d = distance(coord, pos);

    float redMouse = 1. / (1. + 0.03 * d);

    red = max(red, redMouse);

    fragColor = vec4(red, 0.0, 0.0, 1.0);
}
