// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // strip attri
    float stripNum = 7.0;
    float angle = 45.0;
    float rad = radians(angle);
    float combinedUV = uv.x * cos(rad) + uv.y * sin(rad);
    float mirroredStrip_red = 1.0 - abs(1.0 - 2.0 * mod(combinedUV * stripNum, 1.0));
    float uv_green = combinedUV + 0.05 * iTime;
    float mirroredStrip_green = 1.0 - abs(1.0 - 2.0 * mod(uv_green * stripNum, 1.0));
    float uv_blue = combinedUV - 0.05 * iTime;
    float mirroredStrip_blue = 1.0 - abs(1.0 - 2.0 * mod(uv_blue * stripNum, 1.0));
    float pattern_red = smoothstep(0.45, 0.65, mirroredStrip_red);
    float pattern_green = smoothstep(0.45, 0.65, mirroredStrip_green);
    float pattern_blue = smoothstep(0.45, 0.65, mirroredStrip_blue);

    // Time varying pixel color
    float r = mix(1.0, 0.0, pattern_red);
    float g = mix(1.0, 0.0, pattern_green);
    float b = mix(1.0, 0.0, pattern_blue);
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
