void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Set the target screen resolution
    vec2 resolution = vec2(iResolution.y, iResolution.y);
    float aspect = resolution.x / resolution.y;

    // Set square size in terms of screen height
    float squareHeight = 1.0;  // Adjust between 0.0 and 1.0 for relative square height
    vec2 squareSize = vec2(squareHeight * aspect, squareHeight); // Square dimensions

    // Center the coordinates and scale to square
    vec2 uv = fragCoord / resolution - 0.5; // Normalize to -0.5 to 0.5 range
    uv /= squareSize;                       // Scale to fit the square size
    uv += 0.5;                              // Map back to 0-1 range for texture sampling

    uv.x -= 0.4;

    // Only render the texture within the square area
    if (uv.x < 0.0 || uv.x > 1.0 || uv.y < 0.0 || uv.y > 1.0) {
        fragColor = vec4(0.0);               // Outside square area, make transparent
    } else {
        fragColor = texture(iChannel0, uv);  // Inside square, map the texture

        float avg = (fragColor.x + fragColor.y +fragColor.z)/3.0;
        if(avg > 0.2 + abs(.3*sin(.1*iTime))){
            fragColor = vec4(vec3(1.0),0.0);
        }else{
            fragColor = vec4(vec3(cos(iTime),sin(iTime),tan(iTime)),0.0);
        }
    }
}
