void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 uv2 = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;

    float windSize = 0.25;
    float offset = 0.02 * (sin(20.0 * uv2.x) > 0.0 ? 1.0 : -1.0);
    float flip = fract(iTime) > 0.50 ? 1.0 : -1.0;

    float boundTime = 0.5 + offset * flip;
    float bBound = max(0.0, boundTime - windSize);
    float tBound = min(1.0, boundTime + windSize);

    float extra = 0.0;

    if (uv.y < tBound && uv.y > bBound)
    {
        float y = (uv.y - bBound) / (tBound - bBound);
        extra = y;
        if (uv2.x > 1.5) extra = 0.0;
    }


	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz;

    col *= extra * (1.0 - nyan.w);
    if (nyan.w > 0.0)
    {
        col += nyan.xyz;
    }

    fragColor = vec4(col, 1.0 );
}
