vec4  InterlaceColor = vec4(0.0, 0.0, 0.0, 1.0);     // Change color of interlaced lines here
vec4  NoiseColor     = vec4(0.0, 0.0, 0.0, 1.0);     // Change color of the noise lines here
float NoiseStrength  = 0.50;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;



    float noise = (fract(sin(dot(uv.xy * iTime, vec2(12.9898,78.233))) * 43758.5453) - 0.5) * 2.0;



    // uv.x += iTime / 10.0;
    // uv.y += iTime / 30.0;
    vec4 color = texture(iChannel0, uv);

    // 2 pixels wide, change for more/less
    // or change <fragCoord.y> to <fragCoord.x for vertical lines
    if (((int(fragCoord.y) % 4 == 0) || (int(fragCoord.y) % 4 == 1))) {   
        color.rgba += (noise + NoiseColor) * NoiseStrength;
        fragColor = InterlaceColor;
    }
    else {
        color.rgba += (noise + NoiseColor) * NoiseStrength;
        fragColor = color;
    } 
}
