void mainImage(out vec4 O, vec2 U) {
        float N = 65., i, j, t = iTime, T;
        vec2 R = iResolution.xy,
                p = N * (U + U - R) / R.y, w, v;
        i = round(p.x);
        p.y += T = fract(t + fract(234.567 * sin(i * 123.456)));
        w = vec2(58, -49) + 3. * sin(i / N * vec2(16, 6) + t * 3.);
        v = round(p.y - w) + w;
        j = p.y < 0. ? v.y : v.x;
        if (j >= w.y && j <= w.x) j = max(w.y, w.x - 2e2 * T * T);
        O = vec4(0, p.y<-28., p.y/33.-.02, 1) * .5 + smoothstep(3. * N / R.y, 0., length(p - vec2(i, j)) - 1.5);
}       //  color elsio


