void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 fc=ivec2(fragCoord);
    if (0==(iFrame+fc.x+fc.y&1)) discard; //PenTile
    fc.x>>=1; //half screen to full screen
    fragColor = texelFetch( iChannel0, fc , 0 );
}

void mainImage( out vec4 O, vec2 u )
{
    if (u.x+u.x>iResolution.x) discard; //draw half screen each frame
    u.x-=.5*float((int(u.y)+iFrame)&1); //PenTile shift
    vec2  z = vec2(.4,.2),
          S = z*.03/iResolution.y,
          U = (u - iMouse.xy)*S + vec2(-1.469,-.278);
    vec4  C;
    O=C;
    S*=z; //pattern for MSAA
    for (int i, j; i++<4; S=vec2(S.y,-S.x), O+=C/float(j)) // MSAA 4x
        for (z-=z, C-=C, j=-2; j++ < 999 && dot(z,z) < 1e9;)
            z = mix( min(exp(z.x),1e9) * vec2( cos(z.y), sin(z.y) ),
                     mat2(z,-z.y,z.x) * z,                           
                    .666 )
                + U + S,
            C[ int(z.x<0.)*2 + int(z.y<0.) ]++;          

    O = cos( O.xzww * vec4(9,-6.5,16.3,0) );
}
