vec3 cos_palette(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;


    vec2 uvCopy = uv;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;

    vec3 bg = vec3(1, 1., 1.);

    float wave = sin(uvCopy.x * 15.0 + (iTime * 2.0)) * 0.05;    

    uvCopy.y += wave;

    float rainbowWidth = 0.1;

    if(uvCopy.x < 1.45 && ((uvCopy.y - 0.5) <= rainbowWidth && (uvCopy.y - 0.5) >= -rainbowWidth))
    {
        float t = sin(uvCopy.y) * 4.0;
        bg = cos_palette(t, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
    }


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = mix(bg, nyan.xyz, nyan.a);

    fragColor = vec4( col, 1.0 );
}
