#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC ){

float i,j,d=.1,e; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*16.;
    p=mod(p-cos(p-.5),4.)-1.2;
    p.zx*=R2D(iTime);
    p.yz*=R2D(iTime);

    for(j=0.;j++<8.;)
        p=1./abs(abs(p*mix(p,q,.34))),//*cos(s),
        d=dot(1.05,dot(p,p)),
        d-=length(p.xy)/1e4;

    e-=d;

}o+=3./i;
}
