void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    uv.y = 1.0 - uv.y;
    vec2 scale = vec2(4.0, 6.0);
    uv *= scale;

    if (int(uv.y) % 2 == 0)
        uv.x += 0.5;

    uv = fract(uv);

    vec2 thickness = vec2(0.025, 0.05);

    bool mortar = (uv.x < thickness.x) || (uv.y < thickness.y) ||
                  (uv.x > 1.0 - thickness.x) || (uv.y > 1.0 - thickness.y);

    vec3 brickColor = vec3(200, 94, 55) / 255.0;
    vec3 mortarColor = vec3(0.7, 0.7, 0.7);

    vec3 col = mortar ? mortarColor : brickColor;

    fragColor = vec4(col,1.0);
}
