void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    fragColor = vec4(texelFetch(iChannel0, ivec2(fragCoord), 0).xyz, 1.0);
}

const float mouseRadius = 0.2;





const int resetKey = 82;

void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    ivec2 coord = ivec2(fragCoord);

    if (iFrame == 0 || 0.5 < texelFetch(iChannel1, ivec2(resetKey, 0), 0).x) {
        vec2 uv = vec2(coord) / iResolution.xy;
        fragColor = texture(iChannel2, uv);
        return;
    }


    vec2 halfSize = iResolution.xy / 2.;
    int mouseEffect = int(length(halfSize - abs(abs(iMouse.xy - fragCoord) - halfSize)));
    mouseEffect = clamp(int(mouseRadius * iResolution.y) - mouseEffect, 0, 1);
    if (iMouse.z < 0.) mouseEffect = 0;

    #define SAMPLE(x, y) texelFetch(iChannel0, (coord + ivec2(x, y)) % ivec2(iResolution.xy), 0);
    vec4 c = SAMPLE(0, 0);
    vec4 ll = SAMPLE(-2, 0);
    vec4 ld = SAMPLE(-1, -1);
    vec4 lu = SAMPLE(-1, 1);
    vec4 x = SAMPLE(-1, int(lu.w));
    #undef SAMPLE

    vec4 pixel = mix(ll, x, step(c, lu));
    pixel = mix(pixel, c, step(ld, lu));
    pixel.w = float(1 - mouseEffect);

    fragColor = pixel;
}
