// version of
// https://www.shadertoy.com/view/mddyWS

#define r(a) mat2(cos(a + vec4(0, 33, 11, 0)))

void mainImage(out vec4 O, vec2 u) {
    vec3  p = iResolution, S, C; 
          u = (u + u - p.xy) / p.y;  

    float i, d, a, T = iTime * .5;

    O *= 0.;
    while(i++ < 120.){
        p = .6 * vec3(u * a, a - .3);

        p.yz *= r(T); d = 6. / dot(p, p);
        p *= d * .4;
        p.xz *= r(p.y * sin(T) *.1);

        int j; 
        while(j++ < 9) 
            p.z = abs(p.z), 
            p.xz *= r(3.14/9.);

        C = cos(p * p.z / 20.);
        S = sin(p);

        p = abs(S) * C * 8. + 3.;

        a += d = p.y / (2.5 * d);

        if(dot(S, p) < 1.25)
            O.r += .01 / exp(1200. * d);

        if(dot(C, mix(S, p, cos(T*.6)*.5+.5)) > 1.25)
            O.g = clamp(O.g +  .01 / exp(9000. * d + .17), 0., 1.);
        else
            O.b = cos(C.z + T*14.) * sin(S.y);
    }
}
