#define N 10. 
void mainImage(out vec4 fragColor, vec2 fragCoord) 
{
    fragCoord = (fragCoord*2.-(fragColor.xy=iResolution.xy))/fragColor.y;
    float r = length(fragCoord), a = atan(fragCoord.y, fragCoord.x), i = r * N;
    a *= pow(128.0, i / N);
    a += 20.0 * sin(0.5 * iTime) + 123.34 * i - 100.0 * r * cos(0.5 * iTime);
    r += 0.5 + 0.5 * cos(a);
    r = floor(N * clamp(r, 0.0, 1.0)) / N; 
    vec3 color = vec3(0.5, 1.0, 1.8) * (1.0 - r);
    fragColor = vec4(color, 1.0);
}
