float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

vec3 palette( float t ) 
{
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

   vec2 uv0 = uv;

   vec3 finalColor = vec3(0.0);

   float tb = 0.5 - 0.5 * cos(iTime / 1.5);  

   float d = sdCircle( uv , tb);  


   for (float i = 0.0; i < 4.0; i++);
   {    
       uv = fract(uv0 * 0.5) - 0.5;

       float d2 = length(uv) * exp(-length(uv0));

       vec3 col = palette(length(uv) + iTime / 1.5);

       float tb2 = 0.25 - 0.25 * cos(iTime / 1.5);  

       float d3 = sdCircle( uv0 , tb2);  

       d = sin(d*8. + iTime)/8.;
       d = abs(d);

       d2 = pow(0.025 / d, 3.0);
       d3 = pow(0.05 / d2, 2.0);

       finalColor += col * d / d2 / d3;
    }

    fragColor = vec4(finalColor ,1.0);
}
