// Example Video: https://www.youtube.com/watch?v=uCeFcoSVxIY

float MAX = 0.5;
// wireframe code from FabriceNeyret2: https://www.shadertoy.com/view/XfS3DK
#define FFT(a) pow(texelFetch(iChannel1, ivec2(a, 0), 0).x, 5.)
#define O(x,a,b) smoothstep(0., 1., cos(x*6.2832)*.5+.5)*(a-b)+b  // oscillate between a & b
#define A(v) mat2(cos((v*3.1416) + vec4(0, -1.5708, 1.5708, 0)))  // rotate
#define s(p1, p2) c += .02/abs(L( u, K(p1, v, h), K(p2, v, h) )+.01)*k;  // segment
float snd = 0.;
// line
float L(vec2 p, vec3 A, vec3 B)
{
    vec2 a = A.xy, 
         b = B.xy - a;
         p -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    if(snd>MAX)snd=MAX;
    return length(p - b*h)*(0.9-snd*1.9) + .01*mix(A.z, B.z, h)*(1.+snd);
    //return length(p - b*h)*(0.8-snd*1.9+FFT(25)+(FFT(1))) + .01*mix(A.z, B.z, h)*(1.+snd);
}

// MIT Licensed hash From Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
vec3 hash33(vec3 p)
{
    p = fract(p * vec3(443.8975,397.2973, 491.1871));
    p += dot(p.zxy, p.yxz+19.27);
    return fract(vec3(p.x * p.y, p.z*p.x, p.y*p.z));
}

vec3 stars(in vec3 p)
{
    vec3 c = vec3(0.);
    float res = iResolution.x*0.8;

	for (float i=0.;i<4.;i++)
    {
        vec3 q = fract(p*(.15*res))-0.5;
        //q*= snd/10.;
        vec3 id = floor(p*(.15*res));
        vec2 rn = hash33(id).xy;
        float c2 = 1.-smoothstep(0.,.6,length(q));
        c2 *= step(rn.x,.0005+i*i*0.001);
        c += c2*(mix(vec3(1.0,0.49,0.1),vec3(0.75,0.9,1.),rn.y)*0.25+0.75);
        p *= 1.4;
    }
    return c*c*.65;
}

// colormap
vec3 palette(float t) {
    if(t <1.)t+=1.;
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.);
    //vec3 d = vec3(0.563,0.416,0.457 + .2);
    vec3 d = vec3(0.563,0.416,0.257 + .2);

    //return a + b*cos( 6.28 * c * (t+d)); // A + B * cos ( 2pi * (Cx + D) )
    return a + b*cos( 6.28 * c * (t+d)); // A + B * cos ( 2pi * (Cx + D) )
}
// cam
vec3 K(vec3 p, mat2 v, mat2 h)
{
    p.zy *= v; // pitch
    p.zx *= h; // yaw
    if (texelFetch(iChannel0, ivec2(80, 2), 0).x < 1.) // P key
        p *= 6. / (p.z+6.); // perspective view
    return p;
}

void mainImage( out vec4 C, in vec2 U )
{
    int max_freq = 100;
    for(int i=1; i < max_freq; i++){
        snd +=FFT(i)*float(i);
        //snd +=FFT(i);
    }
    snd /=float(max_freq*20);
    //snd /=float(max_freq);
    //snd*=2.8;
    //snd*=1.8;
    snd*=1.6;
    //snd*=2.;

    vec2 R = iResolution.xy,
         u = (U+U-R)/R.y*2.,
         m = (iMouse.xy*2.-R)/R.y;

    float t = iTime/60.,
          o = t*6.,
          //o = t*3.*snd,
          j = (u.x > 0.) ? 1.: -1.; // screen side
    /*
    if (iMouse.z < 1.) // not clicking
        m = vec2(sin(t*6.2832)*2., sin(t*6.2832*2.)); // fig-8 movement
    */
    mat2 v = A(m.y), // pitch
         h = A(m.x); // yaw

    vec3 c = vec3(0), p, 
         k = vec3(2,1,4)/40. + .05;

    //u.x -= j + j*.7; // split screen
    u.x+=sin(iTime/4.)/10.;
    u.y+=sin(iTime/4.)/10.;
    //u*=.5+(sin(iTime/10.))/2.;
    u*=1.-(sin(iTime/10.))/2.;
    snd*=1.1;
    {
        //p = vec3(0, .618, 1);  // stellated dodecahedron
        //p = vec3(0, 1, -.618); // icosahedron

        // stella dodeca to icosa
        p = vec3(0, O(o, .618, 1.), O(o, 1., -.618));
        //p.z+=(1.+FFT(1));
        p.x+=sin(iTime);
	s( vec3(-p.y+FFT(1),  p.z+snd,    0), vec3(   0, -p.y+FFT(1), -p.z) )
        s( vec3(-p.y+FFT(1),  p.z+snd,    0), vec3(   0, -p.y+FFT(1),  p.z+snd) )
        s( vec3(-p.y+FFT(1),  p.z+snd,    0), vec3( p.z+snd,    0, -p.y+FFT(1)) )
        s( vec3(-p.y+FFT(1),  p.z+snd,    0), vec3( p.z+snd,    0,  p.y*snd) )
        s( vec3( p.y*snd,  p.z+snd,    0), vec3( p.y*snd, -p.z,    0) )
        s( vec3( p.y*snd,  p.z+snd,    0), vec3(   0, -p.y+FFT(1), -p.z) )
        s( vec3( p.y*snd,  p.z+snd,    0), vec3(   0, -p.y+FFT(1),  p.z+snd) )
        s( vec3( p.y*snd,  p.z+snd,    0), vec3(-p.z,    0, -p.y+FFT(1)) )
        s( vec3( p.y*snd,  p.z+snd,    0), vec3(-p.z,    0,  p.y*snd) )
        s( vec3(-p.y+FFT(1), -p.z,    0), vec3(-p.y+FFT(1),  p.z+snd,    0) )
        s( vec3(-p.y+FFT(1), -p.z,    0), vec3(   0,  p.y*snd, -p.z) )
        s( vec3(-p.y+FFT(1), -p.z,    0), vec3(   0,  p.y*snd,  p.z+snd) )
        s( vec3(-p.y+FFT(1), -p.z,    0), vec3( p.z+snd,    0, -p.y+FFT(1)) )
        s( vec3(-p.y+FFT(1), -p.z,    0), vec3( p.z+snd,    0,  p.y*snd) )
        s( vec3( p.y*snd, -p.z,    0), vec3(   0,  p.y*snd, -p.z) )
        s( vec3( p.y*snd, -p.z,    0), vec3(   0,  p.y*snd,  p.z+snd) )
        s( vec3( p.y*snd, -p.z,    0), vec3(-p.z,    0, -p.y+FFT(1)) )
        s( vec3( p.y*snd, -p.z,    0), vec3(-p.z,    0,  p.y*snd) )
        s( vec3(   0,  p.y*snd, -p.z), vec3(   0,  p.y*snd,  p.z+snd) )
        s( vec3(   0,  p.y*snd, -p.z), vec3( p.z+snd,    0,  p.y*snd) )
        s( vec3(   0,  p.y*snd, -p.z), vec3(-p.z,    0,  p.y*snd) )
        s( vec3(   0, -p.y+FFT(1), -p.z), vec3(   0, -p.y+FFT(1),  p.z+snd) )
        s( vec3(   0, -p.y+FFT(1), -p.z), vec3( p.z+snd,    0,  p.y*snd) )
        s( vec3(   0, -p.y+FFT(1), -p.z), vec3(-p.z,    0,  p.y*snd) )
        s( vec3(-p.z,    0, -p.y+FFT(1)), vec3( p.z+snd,    0, -p.y+FFT(1)) )
        s( vec3(-p.z,    0,  p.y*snd), vec3( p.z+snd,    0,  p.y*snd) )
        s( vec3(-p.z,    0, -p.y+FFT(1)), vec3(   0,  p.y*snd,  p.z+snd) )
        s( vec3(-p.z,    0, -p.y+FFT(1)), vec3(   0, -p.y+FFT(1),  p.z+snd) )
        s( vec3( p.z+snd,    0, -p.y+FFT(1)), vec3(   0,  p.y*snd,  p.z+snd) )
        s( vec3( p.z+snd,    0, -p.y+FFT(1)), vec3(   0, -p.y+FFT(1),  p.z+snd) )
    }
    snd/=1.1;
    //c*=palette(snd*2.);
    c*=palette(snd*1.5+(sin(iTime)));
    vec3 rd = normalize(vec3(u,-1.5));
    rd.x+=sin(iTime/1000.);
    vec3 bg = stars(rd);//*(1.+30.*snd);
    c+=bg;

    C = vec4(c + c*c, 1);
}
