#define PSD pow(textureLod(iChannel0, vec2(.3), 0.).r, 2.)
void mainImage(out vec4 a, in vec2 b)
{
    a -= a;
    float x = 1.0, i = x, T = iTime, l;
    vec2 res = iResolution.xy;
    vec4 p = vec4((b + b - res) / res.y, .4, 0), d = p;
    p.y -= 7.; p.x -= T + T;
    p *= .3;

    for (; i > 0. && x > 1e-3; i -= .02)
        x = length(cos(p.wxw) - cos(p.yzw)) - PSD * .3,
        a = vec4(1.0, 0.5, 0.0, 1.0) * i * i * vec4(l = distance(p, p - p), 2. / l * l, abs(p.y), 1.),
        p -= d * x * .7;
}
